/**
 * Types of errors which may occur during calls to BSHC
 *
 * @author Christopher Holomek
 * @since 06.01.2020
 */
export declare enum BshbErrorType {
    /**
     * undefined error
     */
    ERROR = 0,
    /**
     * aborted by client
     */
    ABORT = 1,
    /**
     * timeout during call
     */
    TIMEOUT = 2,
    /**
     * jsonrpc error during polling
     */
    POLLING = 3,
    /**
     * Could not parse response
     */
    PARSING = 4
}
